mapper.select:
SELECT c.id AS menuId, c.menuCode AS menuCode, c.menuName AS menuName, c.menuUrl AS menuUrl, c.menuIcon AS menuIcon, c.parentCode AS parentCode, c.seqSort as seqSort FROM k12system_role_post a LEFT JOIN k12system_role_menu b ON a.roleCode = b.roleCode LEFT JOIN k12system_menu c ON b.menuCode = c.menuCode WHERE a.`status` = 0 AND b.`status` = 0 AND c.`status` = 0 AND c.parentCode = #{parentCode}
AND a.postCode = #{postCode}
ORDER BY c.seqSort ASC
